mapper.select:
 		select c.client_id,c.client_name,c.phone,c.sex,c.client_type,c.state,c.consumption_times,c.total_consumption_amount,s.staff_id,s.staff_name from client_resource c,staff s where c.staff_id = s.staff_id and c.staff_id=#{staffId}
		
			and c.client_name like #{clientName}
		
		
			and c.phone = #{phone}
