mapper.select:
select s.staff_id,s.staff_name,s.sex,d.department_abbreviation,r.role_name,s.state,s.last_time,s.remarks_info,(select staff_name from staff where staff_id = s.operation_person) operationPersonName from staff s,department d,role r where s.department_id = d.department_id and s.role_identity = r.role_id

	and s.user_name = #{userName}


	and s.contact_mode = #{contactMode}


	and s.staff_id = #{staffId}

mapper.update:
update staff
remarks_info = #{remarksInfo}, department_id = #{departmentId}, state = #{state}, sex = #{sex}, staff_name = #{staffName}, contact_mode = #{contactMode}, user_name = #{userName}, password = #{password}
where staff_id = #{staffId}
